mapper.select:
select
u.id as id,
u.user_name as user_name,
ifnull(u.nick_name, tu.nick_name) as nick_name,
u.person_sign as person_sign,
ifnull(u.photo, tu.img) as photo,
u.email as email,
u.phone as phone,
u.password as password,
u.prefer_natural as prefer_natural,
u.prefer_human as prefer_human,
u.prefer_running as prefer_running,
u.prefer_play_time as prefer_play_time,
u.prefer_night_play as prefer_night_play,
u.prefer_pub_trans_first as prefer_pub_trans_first,
u.prefer_hotel_level as prefer_hotel_level,
u.create_time as create_time,
u.prefer_trip_number as prefer_trip_number,
u.prefer_flight as prefer_flight,
u.area_code as area_code,
u.tim_sync as tim_sync,
u.tim_identifier as tim_identifier,
u.is_spider as is_spider,

tu.user_id is not null as third_user_type,
tu.type as type,
tu.openid as openid
from
tb_user as u
left join tb_third_user as tu on  tu.user_id = u.id


        u.id = #{id}


        and u.user_name = #{userName}


        and u.nick_name = #{nickName}


        and u.person_sign = #{personSign}


        and u.photo = #{photo}


        and u.email = #{email}


        and u.phone = #{phone}


        and u.password = #{password}



            and u.prefer_natural = #{preferNatural}


            and u.prefer_human = #{preferHuman}


            and u.prefer_running = #{preferRunning}


            and u.prefer_play_time = #{preferPlayTime}


            and u.prefer_night_play = #{preferNightPlay}


            and u.prefer_pub_trans_first = #{preferPubTransFirst}


            and u.prefer_hotel_level = #{preferHotelLevel}


            and u.prefer_trip_number = #{preferTripNumber}


            and u.prefer_flight = #{preferFlight}



        and u.area_code = #{areaCode}


        and u.create_time = #{createTime}


        and u.tim_identifier = #{timIdentifier}


limit 1
