mapper.select:
select
id,
user_name,
nick_name,
person_sign,
photo,
email,
phone,
password,
prefer_natural,
prefer_human,
prefer_running,
prefer_play_time,
prefer_night_play,
prefer_pub_trans_first,
prefer_hotel_level,
create_time,
prefer_trip_number,
prefer_flight,
area_code,
tim_sync,
tim_identifier,
is_spider
from
xunlu.tb_user


        id = #{id}


        and user_name = #{userName}


        and nick_name = #{nickName}


        and person_sign = #{personSign}


        and photo = #{photo}


        and email = #{email}


        and phone = #{phone}


        and password = #{password}



            and prefer_natural = #{preferNatural}


            and prefer_human = #{preferHuman}


            and prefer_running = #{preferRunning}


            and prefer_play_time = #{preferPlayTime}


            and prefer_night_play = #{preferNightPlay}


            and prefer_pub_trans_first = #{preferPubTransFirst}


            and prefer_hotel_level = #{preferHotelLevel}


            and prefer_trip_number = #{preferTripNumber}


            and prefer_flight = #{preferFlight}



        and area_code = #{areaCode}


        and create_time = #{createTime}


        and tim_identifier = #{timIdentifier}


limit 1
